package main

import (
	"fmt"
	"log"
	"net/http"
)

// This would be auto-generated by goxc from Counter.gox
// For demonstration purposes, showing what the output would look like

func main() {
	http.HandleFunc("/", handleIndex)
	http.HandleFunc("/styles.css", handleStyles)

	port := ":8080"
	fmt.Printf("ðŸš€ Server running on http://localhost%s\n", port)
	log.Fatal(http.ListenAndServe(port, nil))
}

func handleIndex(w http.ResponseWriter, r *http.Request) {
	// Create counter component with initial value
	counter := NewCounter(0)

	// Render component to HTML
	html := renderPage(counter.Render())

	w.Header().Set("Content-Type", "text/html; charset=utf-8")
	fmt.Fprint(w, html)
}

func handleStyles(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/css; charset=utf-8")
	fmt.Fprint(w, generatedStyles)
}

func renderPage(content string) string {
	return fmt.Sprintf(`<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>GoX Counter - SSR Example</title>
    <link rel="stylesheet" href="/styles.css">
    <style>
        body {
            margin: 0;
            padding: 0;
            background: linear-gradient(135deg, #667eea 0%%, #764ba2 100%%);
            min-height: 100vh;
        }
    </style>
</head>
<body>
    <div id="app">
        %s
    </div>

    <script>
        // Client-side hydration would go here
        // For now, this is a static render
    </script>
</body>
</html>`, content)
}

